mapper.select:
--작가목록 조회 --fwk.sample.Sample.selectAuthorList SELECT AUTHOR_ID AS AUTHOR_VIEW --그리드PUPUPLINK용 , AUTHOR_ID --작가ID , AUTHOR_NM --이름 , BIRTH_DAY --생년월일 , DEBUT_YEAR --데뷔년도 , TOT_ACCMLT_INCME --총누적수입 , REGIST_STTUS --등록상태 , LAST_UPDT_PNTTM --최종수정시점 , LAST_UPDUSR_ID --최종수정자ID FROM Z_AUTHOR WHERE 1=1
AND AUTHOR_NM LIKE '%'||#{AUTHOR_NM}||'%'
AND DEBUT_YEAR >= #{DEBUT_YEAR_BGN}
AND DEBUT_YEAR <= #{DEBUT_YEAR_END}

mapper.update:
--작가정보 수정
--fwk.sample.Sample.updateAuthor
UPDATE Z_AUTHOR
   SET AUTHOR_NM       = #{AUTHOR_NM}
     , BIRTH_DAY       = #{BIRTH_DAY}
     , HISTORY         = #{HISTORY}
     , DEBUT_YEAR      = #{DEBUT_YEAR}
     , LAST_UPDT_PNTTM = SYSDATE
     , LAST_UPDUSR_ID  = #{SESSION_USER_ID}
 WHERE AUTHOR_ID = #{AUTHOR_ID}

mapper.insert:
--책 등록
--fwk.sample.Sample.insertBook
INSERT INTO Z_BOOK(
	  BOOK_ID
	, TITLE
	, TOTAL_PAGE
	, AUTHOR_ID
	, FRST_REGIST_PNTTM
	, FRST_REGISTER_ID
	, LAST_UPDT_PNTTM
	, LAST_UPDUSR_ID
)
VALUES(
	  #{BOOK_ID   }
	, #{TITLE     }
	, #{TOTAL_PAGE}
	, #{AUTHOR_ID }
	, SYSDATE
	, #{SESSION_USER_ID}
	, SYSDATE
	, #{SESSION_USER_ID}
)

mapper.update:
	--책 수정
	--fwk.sample.Sample.updateBook
	UPDATE Z_BOOK
	   SET TITLE           = #{TITLE}
		 , TOTAL_PAGE      = #{TOTAL_PAGE}
		 , LAST_UPDT_PNTTM = SYSDATE
		 , LAST_UPDUSR_ID  = #{SESSION_USER_ID}
     WHERE BOOK_ID = #{BOOK_ID}

--fwk.sample.Sample.updateAuthorName
UPDATE Z_AUTHOR
   SET AUTHOR_NM = #{AUTHOR_NM}
 WHERE AUTHOR_ID = #{AUTHOR_ID}

--작가 첨부파일 일련번호 UPDATE
--fwk.sample.Sample.updateAuthorAttachFileId
UPDATE Z_AUTHOR
   SET LAST_UPDT_PNTTM = SYSDATE
	 , LAST_UPDUSR_ID  = #{SESSION_USER_ID}

     , ATCH_FILE_ID_A = #{ATCH_FILE_ID_A}


     , ATCH_FILE_ID_B = #{ATCH_FILE_ID_B}

  WHERE AUTHOR_ID = #{AUTHOR_ID}
